import React from 'react';
import Styles from './restaurantInfo.css';


class RestaurantInfo extends React.Component {
  constructor(props){
    super(props);
    
      this.state={
        htmlForRestaurants: "",
      };
    };

    componentDidMount(){
      this.renderRestaurants();
    }

    async renderRestaurants(){
      let restaurants = await fetch('http://localhost:5000/restaurants').then(r => r.json());
      
      let htmlForRestaurants = restaurants.map((cat) => {
        return <a>{cat.nombre}</a>
      });

      this.state.htmlForRestaurants = htmlForRestaurants;
      this.setState(this.state);
      console.log(this.state.htmlForRestaurants);
    }

  render() {
    return (
      <div className="list-restaurant">
        <div className="imageComponent">
          {/* <img class="logo" src={require({this.image})} /> */}
        </div>
        
      </div>
    )
  }
}
export default RestaurantInfo;
