import React from 'react';  
import Styles from './popUp.css'; 
import $ from 'jquery'; 

class Popup extends React.Component {
    constructor(props){
        super(props);
    
          this.state={
            buttons: false,
          };
        };  

    selectButtons = (ingredientes) => {
            if (!this.state.buttons){
                this.setState({ buttons: true });
                console.log(this.state.buttons);
                $('#boton').addClass('PopupIngredientsDisabled list-group-item actived');
            }else{
                this.setState({ buttons: false });
                console.log(this.state.buttons);
                $('#boton').removeClass('PopupIngredients list-group-item');
            }
    }

  render() {  
    console.log(this.props.ingredients);
    return (  
        <div className='popup'>  
            <div className='popup\_inner'>
                <button className="Popup" type="button" onClick={this.props.closePopup}> X </button>
                <h1 className="PopupTitle">{this.props.text}</h1>
                <h2 className="PopupInfo">{this.props.type}</h2>
                    <ul className=" container-ingredients list-group">
                        {
                            this.props.ingredients.map(x => {
                                return (<li className=" PopupIngredients list-group-item "> 
                                            <button type="button" id="boton" className="buttonIngredients" onClick={ () => this.selectButtons(x.ingredientes)}>{x.ingredientes}</button>
                                        </li> );
                            })
                        }
                    </ul>
                <select className="custom-select Cash-tarjet">
                    <option value="1">En Efectivo</option>
                    <option disabled value="2">Con Tarjeta</option>
                </select>
                <button type="button" class="btn Popup-Button" onClick={this.props.closePopup}>Realizar pedido</button>  
            </div>
        </div>
    );
 }  
}  

export default Popup;
