import React from 'react';
import Styles from './listFood.css';
import ReactDOMServer from 'react-dom/server';
import { Redirect } from 'react-router-dom';
import $ from 'jquery';
import Footer from '../../components/Footer/footer.js';

class listFood extends React.Component {
  constructor(props){
    super(props);

      this.state={
        foodInfo: [],
      };
    };

    componentDidMount(){
        this.renderCategories();
        this.renderFood();
      }

    async renderFood(){
        let food = await fetch('http://localhost:5000/food').then(r => r.json());
  
        this.state.foodInfo = food;
        this.setState(this.state);
        console.log(this.state.foodInfo);
      }

    render(){
      return (
        <div className="mainFood">
          <div className="backgroundImageTop">
          </div>
          <div className="content-food">
            <div className="items_filters">
              <ol>
                <li className="filters">Filtros</li>
                <ul>
                  <li className="filters" >Pedido express</li>
                  <li className="filters" >Cupones</li>
                </ul>
                <li className="filters" >Categorias</li>
                <ul>
                  {this.state.htmlForCategories}
                </ul>
              </ol>
            </div>
            <div>
              {this.state.restaurantInfo.map(x => {
                return <RestaurantInfo info={x}/>
              })}
            </div>
          </div>
          <Footer/>
        </div>
      );
    }
};


export default listFood;
